import React from 'react';

function App() {
  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold mb-4">TomatoTime</h1>
        <div className="flex items-center justify-center">
          <div className="w-32 h-32 border-4 border-red-500 rounded-full flex items-center justify-center">
            <span className="text-xl">25:00</span>
          </div>
        </div>
        <div className="mt-4 flex justify-around">
          <button className="bg-red-500 text-white px-4 py-2 rounded">Start</button>
          <button className="bg-gray-500 text-white px-4 py-2 rounded">Stop</button>
          <button className="bg-blue-500 text-white px-4 py-2 rounded">Reset</button>
        </div>
      </div>
    </div>
  );
}

export default App;
