import React from 'react';

function TimerControls({ isActive, onStart, onPause, onReset }) {
  return (
    <div className="flex space-x-4 mt-4">
      {!isActive ? (
        <button
          onClick={onStart}
          className="bg-tomato hover:bg-tomato-dark text-white font-medium py-2 px-6 rounded-lg transition-colors"
        >
          Start
        </button>
      ) : (
        <button
          onClick={onPause}
          className="bg-yellow-500 hover:bg-yellow-600 text-white font-medium py-2 px-6 rounded-lg transition-colors"
        >
          Pause
        </button>
      )}
      <button
        onClick={onReset}
        className="bg-gray-200 hover:bg-gray-300 text-gray-700 font-medium py-2 px-6 rounded-lg transition-colors"
      >
        Reset
      </button>
    </div>
  );
}

export default TimerControls;
